import { ADDTASK, EDITTASK } from "../Action/actiontype";
let firstid=0

const initialState = {
    todos: [
      {
        id:firstid,
        description: "todo 1",
        isDone: true,
      },

      {
        id: firstid,
        description: "todo 2",
        isDone: true,
      },

      {
        id:firstid,
        description: " 3",
        isDone: false,
      },
      
    ],
  };
  
const reducer=(state=initialState , action )=>{

    switch(action.type)
    {
       case(ADDTASK):
       {
        todos: [...state.todos, action.payload]
       }
       

    }


}

export default reducer;
