import React from "react";
import {MDBBtn,MDBTable,MDBTableBody,} from "mdb-react-ui-kit";
import ListGroup from 'react-bootstrap/ListGroup';

const Filter = (props) => {
  let i=0
  const finish=(e)=>{

    e.preventDefault()
    props.todos.isDone==='Done'

  }
  const status=()=>{
    if(props.todos.isDone==="Done")
    {
      return 'Complete';
    }
     else if(props.todos.isDone==="Not-Tet")
     {
      return 'In progress	'
    }
  }
    return (
      <>
      { props.searchfilter==='isDone'
            ? props.todos.filter((todo) => {
            return todo.isDone === true;
                 }) .map((todo) => {
               return (  
                         <MDBTable className="mb-4">
                                 <MDBTableBody>
      <tr>
        <th scope="row">{i+1}</th>
        <td>{todo.description}</td>
        <td></td>
        <td>
          <MDBBtn type="submit" color="danger">
            Delete
          </MDBBtn>
          <MDBBtn type="submit" color="success" className="ms-1" onClick={finish}>
            Finished
          </MDBBtn>

        </td>
      </tr>
                                 </MDBTableBody>
                         
                         </MDBTable>
                      );
                    })

                    : props.searchfilter === "Not-Yet" ? props.todos
      .filter((todo) => {
        return todo.isDone === false;
      })
      .map((todo, i) => (
                 <MDBTable className="mb-4">
                                 <MDBTableBody>
      <tr>
        <th scope="row">{i+1}</th>
        <td>{todo.description}</td>
        <td>{status}</td>
        <td>
          <MDBBtn type="submit" color="danger">
            Delete
          </MDBBtn>
          <MDBBtn type="submit" color="success" className="ms-1">
            Finished
          </MDBBtn>

        </td>
      </tr>
                                 </MDBTableBody>
                         
                         </MDBTable>
      ))

      
      : props.todos.map((todo, i) => (
        <MDBTable className="mb-4">
        <MDBTableBody>
<tr>
<th scope="row">{i+1}</th>
<td>{todo.description}</td>
<td>In progress</td>
<td>
<MDBBtn type="submit" color="danger">
Delete
</MDBBtn>
<MDBBtn type="submit" color="success" className="ms-1">
Finished
</MDBBtn>

</td>
</tr>
        </MDBTableBody>

        </MDBTable>

             ))

             }
             </>
           );


}
         
export default Filter;
