import { ADDTASK, DELETETASK, EDITTASK } from "./actiontype"

export const addtodo=(name)=>
{
    
type:ADDTASK;
payload: name
  
}

export const edittodo=(edit)=>
{
type:EDITTASK
payload:edit
}


export const deletetodo=(del)=>
{   
type:DELETETASK
payload:del
}
