import { ADDTASK, DELETETASK, EDITTASK, FINISHTASK } from "../Action/actiontype";
let firstid=0

export const initialState = {
    todos: [
      {
        id:firstid+1,
        description: "todo 1",
        isDone: true,
      },

      {
        id: firstid+1,
        description: "todo 2",
        isDone: false,
      },

      {
        id:firstid+1,
        description: " todo 3",
        isDone: false,
      },
      
    ],
  };
  
const reducer=(state=initialState , action )=>{

    switch(action.type)
    {
       case(ADDTASK):
       {
        return { ...state, todos: [...state.todos, action.payload] };
       }
       case (FINISHTASK):
        {
          state.todos.map((e) => {
            if (e.isDone === false)
             {
              e.description = action.payload.description;
              todo.isDone = action.payload.isDone;
            }
          });  
        }
       case(EDITTASK):
       {
        state.todos.forEach((todo) => {
            if (todo.id === action.payload.id) {
              todo.description = action.payload.description;
              todo.isDone = action.payload.isDone;
            }
          });
        }

        case (DELETETASK):
        {
            let arr = state.todos.filter((todo) => {return todo.id !== action.payload.id})
            return {...state, todos: [...arr]} 
        }
        default:
            return state;

    }


}

export default reducer;
