import React from "react";
import {MDBBtn,MDBTableBody,} from "mdb-react-ui-kit";
import { useState } from "react";
import {DELETETODO} from '../Redux/Action/action'
import { useDispatch } from "react-redux";


const Filter = (props) => {
  let y=0
  let i=1
 const edit=()=>
 {
document.getElementById('EditTask').style.display = "block";


 }

const dispatch=useDispatch()
    return (
      <>
      
      { props.searchfilter === "Done" ? props.todos
      .filter((todo,) => {
        return todo.isDone === true ;
      })
      .map((todo) => (  
<MDBTableBody key={y++}>        
      <tr>
        <th scope="row">{i}</th>
        <td >{todo.description}</td>
        <td >Complete</td>
        <td>
<MDBBtn type="submit" color="danger" onClick={()=>{
  const todoToDelete = {
    id: todo.id,
    description: todo.description,
    isDone:todo.isDone,
  };
  dispatch(DELETETODO(todoToDelete))
  
}
   }>
            Delete
    </MDBBtn>
<MDBBtn type="submit" color="success" className="ms-1" disabled>
      Done
    </MDBBtn>
        </td>
      </tr>
</MDBTableBody>

))


: props.searchfilter === "Not-Yet" ? props.todos
      .filter((todo) => {if(todo.isDone===false)
        {   
             return todo.isDone === false;
        }
        else
        {
          return todo.isDone === true;
        }

      })
      .map((todo) => (
  <MDBTableBody key={y++} >
      <tr>
        <th scope="row">{i+3}</th>
        <td>{todo.description}</td>
        <td>{todo.isDone===false?'In progress':'Complete'}</td>
        <td>
          <MDBBtn type="submit" color="danger" onClick={()=>{
  const todoToDelete = {
    id: todo.id,
    description: todo.description,
    isDone:todo.isDone,
  };
  dispatch(DELETETODO(todoToDelete))
  
}
   }>
            Delete
          </MDBBtn>
          <MDBBtn type="submit" color="success" className="ms-1" onClick={edit} >
            EditTask
          </MDBBtn>

        </td>
      </tr>
</MDBTableBody>
      ))
: props.todos.map((todo) => (
        <MDBTableBody key={y++}>
<tr scope='row'>
<th scope="row">{i+1}</th>
<td>{todo.description}</td>
<td>{todo.isDone===true ? "Complete":"In progress"}</td>
<td>{todo.isDone===true?
<>
<MDBBtn type="submit" color="danger" onClick={()=>{
  const todoToDelete = {
    id: todo.id,
    description: todo.description,
    isDone:todo.isDone,
  };
  dispatch(DELETETODO(todoToDelete))
  
}
   }>
    Delete
    </MDBBtn>
<MDBBtn type="submit" color="success" className="ms-1" disabled>
      Done
    </MDBBtn>
   
</>
:
<>
<MDBBtn type="submit" color="danger" onClick={()=>{
  const todoToDelete = {
    id: todo.id,
    description: todo.description,
    isDone:todo.isDone,
  };
  dispatch(DELETETODO(todoToDelete))
  
}}>
    Delete
    </MDBBtn>   
<MDBBtn type="submit" color="success" className="ms-1" onClick={edit} >
            EditTask
          </MDBBtn>

</>

}
</td>
</tr>
</MDBTableBody>

))

}
 </>
);


}
         
export default Filter ;
