import React from "react";
import {MDBBtn,MDBTable,MDBTableBody,} from "mdb-react-ui-kit";
import { useState } from "react";
import {FINISHTODO,DELETETODO} from '../Redux/Action/action'
import { useDispatch } from "react-redux";

const Filter = (props) => {
  let i=0


const dispatch=useDispatch()

const finishtask=()=>{
  const todoToFinish = {
    id: props.todos.id,
    description: props.todos.description,
    isDone:props.todos.isDone,
  };

  dispatch(FINISHTODO(todoToFinish))
}
const removetask=()=>{
  const todoToDelete = {
    id: props.todos.id,
    description: props.todos.description,
    isDone:props.todos.isDone,
  };
  dispatch(DELETETODO(todoToDelete))
  
}
   
    return (
      <>

      { 
props.searchfilter === "Done" ? props.todos
      .filter((todo) => {
        return todo.isDone === true ;
      })
      .map((todo) => (  
<MDBTable className="mb-4">
                         <MDBTableBody>
                        
      <tr>
        <th scope="row"  >{i+1}</th>
        <td  >{todo.description}</td>
        <td  >Complete</td>
        <td>
          <MDBBtn type="submit" color="danger" onClick={removetask}>
            Delete
          </MDBBtn>
          <MDBBtn type="submit" color="success" className="ms-1" disabled>
      Done
    </MDBBtn>



        </td>
      </tr>
                                 </MDBTableBody>
                         
                         </MDBTable>
                         
                      ))


: props.searchfilter === "Not-Yet" ? props.todos
      .filter((todo) => {
        return todo.isDone === false;
      })
      .map((todo) => (
                         <MDBTable className="mb-4">
  <MDBTableBody>
      <tr>
        <th scope="row">{i+1}</th>
        <td>{todo.description}</td>
        <td>{todo.isDone===false?'In progress':'Complete'}</td>
        <td>
          <MDBBtn type="submit" color="danger" onClick={removetask}>
            Delete
          </MDBBtn>
          <MDBBtn type="submit" color="success" className="ms-1"  onClick={finishtask} >
            Finished
          </MDBBtn>

        </td>
      </tr>
                                 </MDBTableBody>
                         
                         </MDBTable>
      ))

      
: props.todos.map((todo) => (
        <MDBTable className="mb-4">
        <MDBTableBody>
<tr scope='row'>
<th scope="row">{i+1}</th>
<td>{todo.description}</td>
<td>{todo.isDone===true ? "Complete":"In progress"}</td>
<td>{todo.isDone===true?
<>
<MDBBtn type="submit" color="danger" onClick={removetask}>
    Delete
    </MDBBtn>
<MDBBtn type="submit" color="success" className="ms-1" disabled>
      Done
    </MDBBtn>
   
</>
:
<>
<MDBBtn type="submit" color="danger" onClick={removetask}>
    Delete
    </MDBBtn>   
<MDBBtn type="submit" color="success" className="ms-1" onClick={finishtask}>
    Finished
    </MDBBtn>

</>

}
</td>
</tr>
        </MDBTableBody>

        </MDBTable>

      ))


             }
             </>
           );


}
         
export default Filter ;
